/**
 * @since 1.0.0
 */
export const sign = (n) => {
    return n <= -1 ? -1 : n >= 1 ? 1 : 0;
};
/**
 * @since 1.0.0
 */
export const setoidOrdering = {
    equals: (x, y) => x === y
};
/**
 * @since 1.0.0
 */
export const semigroupOrdering = {
    concat: (x, y) => (x !== 0 ? x : y)
};
/**
 * @since 1.0.0
 */
export const invert = (O) => {
    switch (O) {
        case -1:
            return 1;
        case 1:
            return -1;
        default:
            return 0;
    }
};
